import React, { Component }  from 'react';

class Leagues extends Component {
  state = {
    leagues: []
  }

  componentDidMount() {
    
    fetch("https://api.pandascore.co/ow/leagues?&token=ib5ahXVcsVhUwxLKEggXfrDx9-78AZEYD37rfNGsxeEhQDpM8Ic")
    .then(res => res.json())
    .then((data) => {
      this.setState({ leagues: data })
    })
    .catch(console.log)
  }

  leagueDetails = (event) => {
    console.log("clicked")
   
  }
  
 render(){
  return (
    <div class="list">
      <center><h1>Leagues List</h1></center>
      {this.state.leagues.map((league) => (
        <div class="card" >
          <div className="avatar">
                        <img src={league.image_url} alt={league.name}/>
                      </div>
          <div class="card-body">
            <h5 class="card-title">{league.name}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{league.slug}</h6>
            <p class="card-text">{league.modified_at}</p>
          </div>
          <Link to={`/details/${league.id}`}>
                  <button
                    onClick={() => leagueDetails(league.id)}
                    className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-semibold mr-3 py-2 px-4 rounded-full inline-flex items-center"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      className="feather feather-edit"
                    >
                      <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path>
                      <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path>
                    </svg>
                  </button>
                </Link>
        </div>
      ))}
    </div>
  );
 }
  
}

export default Leagues;
