import React, { Component }  from 'react';

const LeaguesDetails = ({match}) =>{
  
const par = match.params.id;

const fetchDetails = () => {
    fetch(`https://api.pandascore.co/ow/leagues?&token=ib5ahXVcsVhUwxLKEggXfrDx9-78AZEYD37rfNGsxeEhQDpM8Ic/${par}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({ league: data })
    })
    .catch(console.log)

    }
   useEffect(() => {
    fetchDetails();
   }, [])
   return (
    <div>
      <h1>Details {par} </h1>
      <div class="card" >
          <div className="avatar">
                        <img src={this.data.image_url} alt={this.data.name}/>
                      </div>
          <div class="card-body">
            <h5 class="card-title">{this.league.name}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{this.data.slug}</h6>
            <p class="card-text">{this.data.modified_at}</p>
          </div>
          
        </div>
    </div>
  );
 
  
}

export default LeaguesDetails;
