import React, { Component }  from 'react';

class Leagues extends Component {
  state = {
    leagues: [],
    chosenTeam: []
  }

  componentDidMount() {
    
    fetch("https://api.pandascore.co/ow/leagues?&token=ib5ahXVcsVhUwxLKEggXfrDx9-78AZEYD37rfNGsxeEhQDpM8Ic")
    .then(res => res.json())
    .then((data) => {
      this.setState({ leagues: data })
    })
    .catch(console.log)
  }

  selectTeam = (event) => {
    let selectedItem = '';
    if(event.target.className === 'list-item') {
      selectedItem = event.target; 
    } else {
      selectedItem = event.target.parentElement.parentElement;
    }
    let teamName = selectedItem.lastChild.textContent;

    teamName = this.state.teams.filter(team => team.name === teamName);

    console.log(teamName);
    
    this.setState({chosenTeam: teamName});

    console.log(this.state.chosenTeam);

    const teamColumns = R.splitEvery(4, this.state.chosenTeam[0].players);

    this.setState({teamColumns: teamColumns});

    console.log(this.state.teamColumns);
  }
  
 render(){
  return (
    <div class="list">
      <center><h1>Leagues List</h1></center>
      {this.state.leagues.map((league) => (
        <div class="card" key={league.id} className="list-item" onClick={this.selectTeam}>
          <div className="avatar">
                        <img src={league.image_url} alt={league.name}/>
                      </div>
          <div class="card-body">
            <h5 class="card-title">{league.name}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{league.slug}</h6>
            <p class="card-text">{league.modified_at}</p>
          </div>
        </div>
      ))}
    </div>
  );
 }
  
}

export default Leagues;
