const LeaguesDetails = ({match}) =>{
  const state = {
        league: []
      }
const par = match.params.id;

const fetchDetails = () => {
    fetch(`https://api.pandascore.co/ow/leagues?&token=ib5ahXVcsVhUwxLKEggXfrDx9-78AZEYD37rfNGsxeEhQDpM8Ic/${par}`)
    .then(res => res.json())
    .then((data) => {
      this.setState({ league: data })
    })
    .catch(console.log)

}
   
   return (
    <div>
      <h1>Details {par} </h1>
      <div class="card" >
          <div className="avatar">
                        <img src={this.state.league.image_url} alt={this.state.league.name}/>
                      </div>
          <div class="card-body">
            <h5 class="card-title">{this.state.league.name}</h5>
            <h6 class="card-subtitle mb-2 text-muted">{league.slug}</h6>
            <p class="card-text">{this.state.league.modified_at}</p>
          </div>
          <Link to={`/leagues/${league.id}`}>
           <Button variant="primary">Details</Button>
          </Link>
        </div>
    </div>
  );
 
  
}

export default LeaguesDetails;
